import "./App.css";
import PrintItems from "./Components/PrintItems";
import InputList from "./Components/InputList.js";
import { useState } from "react";

const [list, setList] = useState.State(["read book", "notify me", "add water"]);

const appendItem = (item) => {
  setList((prevState)=>{
   return [item,...prevState];
  })
};
function App() {
  // const [List,setList] = useState(['read book','notify me','add water']);

  return (
    <div className="appFile">
      <h1> Hello world</h1>
      <InputList addItem={appendItem} />
      <PrintItems list={list} />
    </div>
  );
}

export default App;
